mapper.insert:
/** board.addBoard **/
INSERT INTO board (
	title
	, content
	, remark
	, start_dt
	, end_dt
	, use_yn
	, del_yn
	, created_dt
	, created_no
) VALUES (
	#{title}
	, #{content}
	, #{remark}
	, #{startDt}
	, #{endDt}
	, #{useYn}
	, #{delYn}
	, #{memberSeq}
	, NOW()
)

mapper.update:
/** board.setBoard **/ UPDATE board
title= #{title}, content= #{content}, remark= #{remark}, start_dt= #{startDt}, end_dt= #{endDt}, use_yn= #{useYn}, del_yn= #{delYn}, updated_dt= NOW(), updated_no= #{memberSeq}
WHERE board_seq= #{boardSeq}

mapper.delete:
/** board.removeBoard **/
DELETE FROM board
WHERE board_seq= #{boardSeq}
