mapper.insert:
/** memo.addMemo **/
INSERT INTO memo (
	memo_seq
	, member_fk
	, memo_date
	, content
	, remark
	, created_dt
	, created_no
) VALUES (
	#{memoSeq}
	, #{memberSeq}
	, #{memoDate}
	, #{content}
	, #{remark}
	, NOW()
	, #{memberSeq}
)

mapper.update:
/** memo.setMemo **/
UPDATE memo 

	 memo_date= #{memoDate},
	 content= #{content},
	 remark= #{remark},
	updated_dt= NOW(), 
	updated_no= #{memberSeq}

WHERE memo_seq= #{memoSeq}

mapper.delete:
		/** memo.removeMemo **/
		DELETE FROM memo
  		WHERE memo_seq= #{memoSeq}
