mapper.insert:
/** post.addPost **/
INSERT INTO post (
	board_fk
	, member_fk
	, thumbnail
	, title
	, content
	, remark
	, start_dt
	, end_dt
	, notice_yn
	, secret_yn
	, use_yn
	, del_yn
	, view_count
	, created_dt
	, created_no
) VALUES (
	#{boardFk}
	, #{memberSeq}
	, #{thumbnail}
	, #{title}
	, #{content}
	, #{remark}
	, #{startDt}
	, #{endDt}
	, #{noticeYn}
	, #{secretYn}
	, #{useYn}
	, #{delYn}
	, 0
	, NOW()
	, #{memberSeq}
)

mapper.update:
/** post.setPost **/ UPDATE post
thumbnail= #{thumbnail}, title= #{title}, content= #{content}, remark= #{remark}, start_dt= #{startDt}, end_dt= #{endDt}, use_yn= #{useYn}, del_yn= #{delYn}, notice_yn= #{noticeYn}, secret_yn= #{secretYn}, updated_dt= NOW(), updated_no= #{memberSeq}
WHERE post_seq= #{postSeq}

/** post.setPostForViewCount **/
UPDATE post SET
	view_count= view_count + 1,
	updated_dt= NOW(),
	updated_no= #{memberSeq}	
WHERE post_seq= #{postSeq}

mapper.delete:
/** post.removePost **/
DELETE FROM zeedeco.post
WHERE post_seq= #{postSeq}
